mapper.insert:
insert into user_basic (
	user_id,
	user_pw,
	user_nm
) values (
	#{userId},
	#{userPwd},
	#{userName}
)

insert into user_detail(
	user_id

		, birth


		, model


		, favorite

) values (
	#{userId}

		, #{birth}


		, #{model}


		, #{favorite}

)

mapper.update:
update user_basic
set user_id = #{userId}

	, user_pw = #{userPwd}


	, user_nm = #{userName}

where user_id like #{userId}

update user_detail
set user_id = #{userId}

	, birth=#{birth}


	, favorite=#{favorite}


	, model=#{model}

where user_id = #{userId}
